// Streamilator Extension v1.2.1
// https://github.com/Scratchify/Streamilator
class ScratchifyExtension {
    constructor (runtime, extensionId) {
		this.icon = 'data:image/svg+xml;base64,';
    		this.menuicon = 'data:image/svg+xml;base64,';
		this.runtime = runtime;
		this.end_hat = 0;
		this.audio_player = new Audio();
		this.set_volume = 1;
		this.version_number = '1.2.1';
    }

    getInfo () {
        return {
            "id": 'scratchify',
            "name": 'Scratchify',
			"blockIconURI": this.icon,
			"menuIconURI": this.menuicon,
            "blocks": [
					{
                	"opcode": 'playAudioFromURL',
                    "blockType": "command",
                    "text": 'play audio [URL]',
					"arguments": {
						"URL": {
							"type": "string",
							"defaultValue": 'https://dev.meower.org/bgm/2.mp3',
						},
					},
                },
					{
                	"opcode": 'stopAudio',
                    "blockType": "command",
                    "text": 'stop audio',
                },
					{
                	"opcode": 'pauseAudio',
                    "blockType": "command",
                    "text": 'pause',
                },
					{
                	"opcode": 'resumeAudio',
                    "blockType": "command",
                    "text": 'resume',
                },
					{
					"opcode": 'goToLocation',
                    "blockType": "command",
                    "text": 'go to location [LOCATION]',
					"arguments": {
						"LOCATION": {
							"type": "number",
							"defaultValue": '0',
						},
					},
                },
					{
					"opcode": 'setAudioVolume',
                    "blockType": "command",
                    "text": 'set volume [VOLUME]',
					"arguments": {
						"VOLUME": {
							"type": "number",
							"defaultValue": '100',
						},
					},
                },
					{
					"opcode": 'setPlaybackSpeed',
					"blockType": "command",
					"text": 'set playback speed [SPEED]',
					"arguments": {
						"SPEED": {
							"type": "number",
							"defaultValue": '1',
						},
					},
				},
				{
                	"opcode": 'enableLoop',
                    "blockType": "command",
                    "text": 'turn loop on',
                },
		    		{
                	"opcode": 'disableLoop',
                    "blockType": "command",
                    "text": 'turn loop off',
                },
		    		{
                	"opcode": 'audioLocation',
                    "blockType": "reporter",
                    "text": 'audio location',
                },
		    		{
                	"opcode": 'audioDuration',
                    "blockType": "reporter",
                    "text": 'audio duration',
                },
		    		{
                	"opcode": 'audioVolume',
                    "blockType": "reporter",
                    "text": 'audio volume',
                },
					{
					"opcode": 'audioSpeed',
					"blockType": "reporter",
					"text": 'audio speed',
				},
					{
					"opcode": 'audioSource',
					"blockType": "reporter",
					"text": 'audio url',
				},
		    		{
                	"opcode": 'audioPaused',
                    "blockType": "Boolean",
                    "text": 'audio paused?',
                },
		    		{
                	"opcode": 'audioEnded',
                    "blockType": "Boolean",
                    "text": 'audio ended?',
                },
		    		{
                	"opcode": 'loopEnabled',
                    "blockType": "Boolean",
                    "text": 'loop enabled?',
                },
					{
                	"opcode": 'whenAudioEnds',
                    "blockType": "hat",
                    "text": 'when audio ends',
                },
					{
                	"opcode": 'versionNumber',
                    "blockType": "reporter",
                    "text": 'extension version',
                },
			]
        };
    };

	playAudioFromURL({URL}) {
		this.audio_player = new Audio(URL);
		this.audio_player.play();
		this.audio_player.volume = this.set_volume
		this.end_hat = 1;
	};

	stopAudio({}) {
		this.audio_player.pause();
		this.audio_player.currentTime = 0;
	};

	pauseAudio({}) {
		this.audio_player.pause();
	};

	resumeAudio({}) {
		this.audio_player.play();
	};

	goToLocation({LOCATION}) {
		this.audio_player.currentTime = LOCATION;
	};

	setAudioVolume({VOLUME}) {
		if (VOLUME >= 0 && VOLUME <= 100) {
 			this.audio_player.volume = VOLUME / 100;
			this.set_volume = VOLUME / 100;
		};
	};

	setPlaybackSpeed({SPEED}) {
		this.audio_player.playbackRate = SPEED;
	}

	enableLoop({}) {
		this.audio_player.loop = true;
	};
	
	disableLoop({}) {
		this.audio_player.loop = false;
	};

	audioLocation({}) {
		return this.audio_player.currentTime;
	};

	audioDuration({}) {
		return this.audio_player.duration;
	};

	audioVolume({}) {
		return this.audio_player.volume * 100;
	};

	audioSpeed({}) {
		return this.audio_player.playbackRate;
	};

	audioSource({}) {
		return this.audio_player.src;
	};

	audioPaused({}) {
		return this.audio_player.paused;
	};

	audioEnded({}) {
		return this.audio_player.ended;
	};

	loopEnabled({}) {
		return this.audio_player.loop;
	};

	whenAudioEnds() {
		if (this.end_hat == 1 && this.audio_player.ended) {
			this.end_hat = 0;
			return true;
		} else {
			return false;
		};
	};

	versionNumber({}) {
		return this.version_number;
	};
};

(function() {
    var extensionClass = ScratchifyExtension;
    if (typeof window === "undefined" || !window.vm) {
        Scratch.extensions.register(new extensionClass());
		console.log("Sandboxed mode detected, performance will suffer because of the extension being sandboxed.");
    } else {
        var extensionInstance = new extensionClass(window.vm.extensionManager.runtime);
        var serviceName = window.vm.extensionManager._registerInternalExtension(extensionInstance);
        window.vm.extensionManager._loadedExtensions.set(extensionInstance.getInfo().id, serviceName);
		console.log("Unsandboxed mode detected. Good.");
    };
})()
